import React from "react";
import OrbitingCircles from "@/components/magicui/orbiting-circles";
import { tailwindcss } from "@/assets/icons";

const CircleItem = ({
  icon,
  radius,
  size = 15,
  reverse,
  duration = 20,
  delay = 20,
}) => {
  return (
    <OrbitingCircles
      className={`size-[${size}px] border-none bg-transparent`}
      duration={duration}
      delay={delay}
      radius={radius}
      reverse={reverse}
    >
      <div className="flex h-16 w-16 items-center justify-center rounded-full bg-white shadow-md">
        {icon}
      </div>
    </OrbitingCircles>
  );
};

export default function OrbitCircles() {
  return (
    <div className="absolute left-0 top-0 flex h-full w-full flex-col items-center justify-center overflow-hidden bg-transparent">
      {/* <span className="pointer-events-none whitespace-pre-wrap bg-gradient-to-b from-black to-gray-300 bg-clip-text text-center text-8xl font-semibold leading-none text-transparent dark:from-white dark:to-black">
        {title}
      </span> */}

      {/* Inner Circles */}
      <CircleItem icon={<Icons.github />} radius={50} duration={5} />
      <CircleItem
        icon={<Icons.html />}
        radius={120}
        reverse={true}
        duration={10}
      />
      <CircleItem icon={<Icons.css />} radius={175} />

      <CircleItem
        icon={<Icons.tailwindcss />}
        radius={225}
        reverse={true}
        duration={20}
      />
      <CircleItem icon={<Icons.react />} radius={300} />
    </div>
  );
}

